import React from "react";
import { View, Text, StyleSheet, TouchableOpacity, Image } from "react-native";

const Chat = () => {
  return (
    <TouchableOpacity>
      <View style={styles.chatContainer}>
        <View style={{width: 70, height: 70, borderRadius: 50, backgroundColor: "#AAA"}}>
            <Image style={styles.tinyLogo}source={require('@expo/snack-static/react-native-logo.png')}/>
            <View style={{width: 20, height: 20, backgroundColor: "green", position: "absolute", bottom: 0, right: -3, borderRadius: 50}}/>
        </View>    
        <View style={{flex: 1, marginHorizontal: 10}}>
            <View style={{flexDirection: "row", justifyContent: "space-between"}}>
                <Text style={{color: "#FFF", fontWeight: "bold", fontSize: 18}}>Username</Text>
                <Text style={{color: "#FFF"}}>9.30</Text>
            </View>
            <Text style={{color: "#AAA", marginVertical: 5}}>Lorem Ipsum is simply dummy text of the printing </Text>
        </View>
      </View>
    </TouchableOpacity>
  );
};

const styles = StyleSheet.create({
    chatContainer:{
        flexDirection: "row",
        marginHorizontal: 15,
        marginTop: 10
    },
  })
export default Chat;
