import React, { useState } from "react";
import {
  StyleSheet,
  View,
  Text,
  TextInput,
  Image,
  TouchableOpacity,
} from "react-native";

const CreatePost = () => {
  return (
    <View style={{ flex: 1, backgroundColor: "#323232", flexDirection: "column", paddingVertical: 20, paddingHorizontal: 10, borderRadius: 10, margin: 10 }}>
      <View style={{flexDirection: "row", alignItems: "center"}}>
        <Image
          source={require("../../../assets/man.png")}
          style={{ resizeMode: "cover", height: 50, width: 50, borderRadius: 50, backgroundColor: "#FF5350", marginRight: 10 }}
        />
        <Text style={{fontWeight: "bold"}}>Username</Text>
      </View>
      <View>
        <TouchableOpacity style={{flex: 1, backgroundColor:"#AAA", marginVertical: 10, padding: 5, borderRadius: 10}}>
          <Text style={{textAlign : "center", fontWeight: "bold"}}>Post</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

export default CreatePost;
