    <View style={{ flex: 1, backgroundColor: "#323232" }}>
        <Image
          source={require("../../assets/logo.png")}
          style={{ resizeMode: "cover", height: 30, width: 100 }}
        />
        <TextInput
            style={{ height: 40, borderColor: 'gray', borderWidth: 1 }}
            value={value}
        />
    </View>
  );
};

export default CreatePost;
