import React, { useState } from "react";
import {
  StyleSheet,
  View,
  Text,
  TextInput,
  Image,
  TouchableOpacity,
} from "react-native";

const Post = () => {
  return (
    <View style={{ flex: 1, backgroundColor: "#323232", flexDirection: "column", paddingVertical: 5, paddingHorizontal: 10, borderRadius: 10, margin: 10 }}>
      <View style={{flexDirection: "row"}}>
        <Image
          source={require("../../../assets/man.png")}
          style={{ resizeMode: "cover", height: 50, width: 50, borderRadius: 50, backgroundColor: "#FF5350", marginRight: 10 }}
        />
         <Text style={{fontWeight: "bold", color: "#FFF", marginRight: 10}}>Username</Text>
         <Text style={{color: "#FFF"}}>50m</Text>
      </View>
      <View>
          <Text style={{textAlign : "center", fontWeight: "bold"}}>Post</Text>
      </View>
    </View>
  );
};

export default Post;
