import React, { useState } from "react";
import {
  StyleSheet,
  View,
  ScrollView,
  Text,
  TextInput,
  Image,
  TouchableOpacity
} from "react-native";

const CreatePost = () => {
  return (
    <View style={{ flex: 1, backgroundColor: "#323232", flexDirection: "row" }}>
      <View>
        <Image
          source={require("../../../assets/man.png")}
          style={{ resizeMode: "cover", height: 50, width: 100 }}
        />
        <TextInput
          style={{ height: 40, borderColor: "gray", borderWidth: 1 }}
        />
      </View>
    </View>
  );
};

export default CreatePost;
